mapper.select:
	SELECT 
      CS_ADJUNTO_ID , 
      NUMERO_ODS , 
      EXTENSION  , 
      DESC_PROVEEDOR , 
      DOCUMENTO_REPOSITORIO_ID , 
      MTR_TIPO_DOCUMENTO_ID , 
      CS_ORDEN_SERVICIO_ID 
	FROM
	CS_ADJUNTO
	WHERE 1 = 1

		AND CS_ADJUNTO_ID = #{id, jdbcType=INTEGER}


		AND NUMERO_ODS = #{numeroOds}


		AND EXTENSION  = #{extension }


		AND DESC_PROVEEDOR = #{descProveedor}


		AND DOCUMENTO_REPOSITORIO_ID = #{documentoRepositorioId}


		AND MTR_TIPO_DOCUMENTO_ID = #{mtrTipoDocumento.id, jdbcType=INTEGER}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO CS_ADJUNTO( CS_ADJUNTO_ID , NUMERO_ODS , EXTENSION , DESC_PROVEEDOR , DOCUMENTO_REPOSITORIO_ID , MTR_TIPO_DOCUMENTO_ID , CS_ORDEN_SERVICIO_ID ) (
SELECT #{element.id} , #{element.numeroOds} , #{element.extension } , #{element.descProveedor} , #{element.documentoRepositorioId} , #{element.mtrTipoDocumento.id} , #{element.csOrdenServicio.id} from dual
)

mapper.delete:
    DELETE FROM CS_ADJUNTO
	WHERE 1 = 1

		AND CS_ADJUNTO_ID = #{id, jdbcType=INTEGER}


		AND NUMERO_ODS = #{numeroOds}


		AND EXTENSION  = #{extension }


		AND DESC_PROVEEDOR = #{descProveedor}


		AND DOCUMENTO_REPOSITORIO_ID = #{documentoRepositorioId}


		AND MTR_TIPO_DOCUMENTO_ID = #{mtrTipoDocumento.id}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id}
