mapper.select:
	SELECT 
      CS_HISTORIAL_ID , 
      DESCRIPCION , 
      USUARIO_HISTORIAL , 
      FECHA_HISTORIAL , 
      MTR_ESTADO_ID , 
      CS_ORDEN_SERVICIO_ID 
	FROM
	CS_HISTORIAL
	WHERE 1 = 1

		AND CS_HISTORIAL_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND USUARIO_HISTORIAL = #{usuarioHistorial}


		AND FECHA_HISTORIAL = #{fechaHistorial, jdbcType=DATE}


		AND MTR_ESTADO_ID = #{mtrEstado.id, jdbcType=INTEGER}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO CS_HISTORIAL( CS_HISTORIAL_ID , DESCRIPCION , USUARIO_HISTORIAL , FECHA_HISTORIAL , MTR_ESTADO_ID , CS_ORDEN_SERVICIO_ID ) (
SELECT #{element.id} , #{element.descripcion} , #{element.usuarioHistorial} , #{element.fechaHistorial} , #{element.mtrEstado.id} , #{element.csOrdenServicio.id} from dual
)

mapper.delete:
    DELETE FROM CS_HISTORIAL
	WHERE 1 = 1

		AND CS_HISTORIAL_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND USUARIO_HISTORIAL = #{usuarioHistorial}


		AND FECHA_HISTORIAL = #{fechaHistorial, jdbcType=DATE}


		AND MTR_ESTADO_ID = #{mtrEstado.id}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id}
