mapper.select:
	SELECT 
      CS_MATERIAL_ID , 
      CODIGO_MATERIAL , 
      DESCRIPCION_MATERIAL , 
      CANTIDAD , 
      MTR_UNIDAD_MEDIDA_ID , 
      CS_ORDEN_SERVICIO_ID 
	FROM
	CS_MATERIAL
	WHERE 1 = 1

		AND CS_MATERIAL_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_MATERIAL = #{codigoMaterial}


		AND DESCRIPCION_MATERIAL = #{descripcionMaterial}


		AND CANTIDAD = #{cantidad, jdbcType=NUMERIC}


		AND MTR_UNIDAD_MEDIDA_ID = #{mtrUnidadMedida.id, jdbcType=INTEGER}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO CS_MATERIAL( CS_MATERIAL_ID , CODIGO_MATERIAL , DESCRIPCION_MATERIAL , CANTIDAD , MTR_UNIDAD_MEDIDA_ID , CS_ORDEN_SERVICIO_ID ) (
SELECT #{element.id} , #{element.codigoMaterial} , #{element.descripcionMaterial} , #{element.cantidad} , #{element.mtrUnidadMedida.id} , #{element.csOrdenServicio.id} from dual
)

mapper.delete:
    DELETE FROM CS_MATERIAL
	WHERE 1 = 1

		AND CS_MATERIAL_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_MATERIAL = #{codigoMaterial}


		AND DESCRIPCION_MATERIAL = #{descripcionMaterial}


		AND CANTIDAD = #{cantidad, jdbcType=NUMERIC}


		AND MTR_UNIDAD_MEDIDA_ID = #{mtrUnidadMedida.id}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id}
