mapper.select:
	SELECT 
      CS_ORDEN_SERVICIO_ACTIVIDAD_ID , 
      DESCRIPCION , 
      CS_ORDEN_SERVICIO_ID 
	FROM
	CS_ORDEN_SERVICIO_ACTIVIDAD
	WHERE 1 = 1

		AND CS_ORDEN_SERVICIO_ACTIVIDAD_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO CS_ORDEN_SERVICIO_ACTIVIDAD( CS_ORDEN_SERVICIO_ACTIVIDAD_ID , DESCRIPCION , CS_ORDEN_SERVICIO_ID ) (
SELECT #{element.id} , #{element.descripcion} , #{element.csOrdenServicio.id} from dual
)

mapper.delete:
    DELETE FROM CS_ORDEN_SERVICIO_ACTIVIDAD
	WHERE 1 = 1

		AND CS_ORDEN_SERVICIO_ACTIVIDAD_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPCION = #{descripcion}


		AND CS_ORDEN_SERVICIO_ID = #{csOrdenServicio.id}
