mapper.select:
	SELECT 
      CS_ORDEN_SERVICIO_ID , 
      NUMERO_ORDEN_SERVICIO , 
      FECHA_EMISION , 
      MOTIVO , 
      TXT_APROBACION , 
      HOROMETRO , 
      FECHA_INICIO_DURACION_REAL , 
      HORA_INICIO_DURACION_REAL , 
      FECHA_FIN_DURACION_REAL , 
      HORA_FIN_DURACION_REAL , 
      CODIGO_SISTEMA , 
      DESCRIPCION_SISTEMA , 
      CODIGO_EMBARCACION , 
      DESCRIPCION_EMBARCACION , 
      CLASE_ODM , 
      RUC_PROVEEDOR , 
      RAZON_SOCIAL_PROVEEDOR , 
      LIFNR_PROVEEDOR , 
      OBSERVACION , 
      CODIGO_EQUIPO , 
      DESCRIPCION_EQUIPO , 
      MTR_ESTADO_ID , 
      ODM_ID , 
      MTR_PUERTO_ID , 
      MTR_MODALIDAD_TRABAJO_ID , 
      MTR_LUGAR_TRABAJO_ID , 
      MTR_CALIDAD_SERVICIO_ID , 
      MTR_TIEMPO_ENTREGA_ID , 
      MTR_SEGURIDAD_ID 
	FROM
	CS_ORDEN_SERVICIO
	WHERE 1 = 1

		AND CS_ORDEN_SERVICIO_ID = #{id, jdbcType=INTEGER}


		AND NUMERO_ORDEN_SERVICIO = #{numeroOrdenServicio}


		AND FECHA_EMISION = #{fechaEmision, jdbcType=DATE}


		AND MOTIVO = #{motivo}


		AND TXT_APROBACION = #{txtAprobacion}


		AND HOROMETRO = #{horometro}


		AND FECHA_INICIO_DURACION_REAL = #{fechaInicioDuracionReal, jdbcType=DATE}


		AND HORA_INICIO_DURACION_REAL = #{horaInicioDuracionReal, jdbcType=DATE}


		AND FECHA_FIN_DURACION_REAL = #{fechaFinDuracionReal, jdbcType=DATE}


		AND HORA_FIN_DURACION_REAL = #{horaFinDuracionReal, jdbcType=DATE}


		AND CODIGO_SISTEMA = #{codigoSistema}


		AND DESCRIPCION_SISTEMA = #{descripcionSistema}


		AND CODIGO_EMBARCACION = #{codigoEmbarcacion}


		AND DESCRIPCION_EMBARCACION = #{descripcionEmbarcacion}


		AND CLASE_ODM = #{claseOdm}


		AND RUC_PROVEEDOR = #{rucProveedor}


		AND RAZON_SOCIAL_PROVEEDOR = #{razonSocialProveedor}


		AND LIFNR_PROVEEDOR = #{lifnrProveedor}


		AND OBSERVACION = #{observacion}


		AND CODIGO_EQUIPO = #{codigoEquipo}


		AND DESCRIPCION_EQUIPO = #{descripcionEquipo}


		AND MTR_ESTADO_ID = #{mtrEstado.id, jdbcType=INTEGER}


		AND ODM_ID = #{odm.id, jdbcType=INTEGER}


		AND MTR_PUERTO_ID = #{mtrPuerto.id, jdbcType=INTEGER}


		AND MTR_MODALIDAD_TRABAJO_ID = #{mtrModalidadTrabajo.id, jdbcType=INTEGER}


		AND MTR_LUGAR_TRABAJO_ID = #{mtrLugarTrabajo.id, jdbcType=INTEGER}


		AND MTR_CALIDAD_SERVICIO_ID = #{mtrCalidadServicio.id, jdbcType=INTEGER}


		AND MTR_TIEMPO_ENTREGA_ID = #{mtrTiempoEntrega.id, jdbcType=INTEGER}


		AND MTR_SEGURIDAD_ID = #{mtrSeguridad.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO CS_ORDEN_SERVICIO( CS_ORDEN_SERVICIO_ID , NUMERO_ORDEN_SERVICIO , FECHA_EMISION , MOTIVO , TXT_APROBACION , HOROMETRO , FECHA_INICIO_DURACION_REAL , HORA_INICIO_DURACION_REAL , FECHA_FIN_DURACION_REAL , HORA_FIN_DURACION_REAL , CODIGO_SISTEMA , DESCRIPCION_SISTEMA , CODIGO_EMBARCACION , DESCRIPCION_EMBARCACION , CLASE_ODM , RUC_PROVEEDOR , RAZON_SOCIAL_PROVEEDOR , LIFNR_PROVEEDOR , OBSERVACION , CODIGO_EQUIPO , DESCRIPCION_EQUIPO , MTR_ESTADO_ID , ODM_ID , MTR_PUERTO_ID , MTR_MODALIDAD_TRABAJO_ID , MTR_LUGAR_TRABAJO_ID , MTR_CALIDAD_SERVICIO_ID , MTR_TIEMPO_ENTREGA_ID , MTR_SEGURIDAD_ID ) (
SELECT #{element.id} , #{element.numeroOrdenServicio} , #{element.fechaEmision} , #{element.motivo} , #{element.txtAprobacion} , #{element.horometro} , #{element.fechaInicioDuracionReal} , #{element.horaInicioDuracionReal} , #{element.fechaFinDuracionReal} , #{element.horaFinDuracionReal} , #{element.codigoSistema} , #{element.descripcionSistema} , #{element.codigoEmbarcacion} , #{element.descripcionEmbarcacion} , #{element.claseOdm} , #{element.rucProveedor} , #{element.razonSocialProveedor} , #{element.lifnrProveedor} , #{element.observacion} , #{element.codigoEquipo} , #{element.descripcionEquipo} , #{element.mtrEstado.id} , #{element.odm.id} , #{element.mtrPuerto.id} , #{element.mtrModalidadTrabajo.id} , #{element.mtrLugarTrabajo.id} , #{element.mtrCalidadServicio.id} , #{element.mtrTiempoEntrega.id} , #{element.mtrSeguridad.id} from dual
)

mapper.delete:
