mapper.select:
	SELECT 
      MTR_UNIDAD_MEDIDA_ID , 
      CODIGO_SAP , 
      DESCRIPCION , 
      STATUS 
	FROM
	MTR_UNIDAD_MEDIDA
	WHERE 1 = 1

		AND MTR_UNIDAD_MEDIDA_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_SAP = #{codigoSap}


		AND DESCRIPCION = #{descripcion}


		AND STATUS = #{status}

mapper.insert:
INSERT INTO MTR_UNIDAD_MEDIDA( MTR_UNIDAD_MEDIDA_ID , CODIGO_SAP , DESCRIPCION , STATUS ) (
SELECT #{element.id} , #{element.codigoSap} , #{element.descripcion} , #{element.status} from dual
)

mapper.delete:
    DELETE FROM MTR_UNIDAD_MEDIDA
	WHERE 1 = 1

		AND MTR_UNIDAD_MEDIDA_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_SAP = #{codigoSap}


		AND DESCRIPCION = #{descripcion}


		AND STATUS = #{status}
