mapper.select:
	SELECT 
      ODM_ID , 
      CODIGO_EQUIPO_SAP , 
      CODIGO_PROVEEDOR_SAP 
	FROM
	ODM
	WHERE 1 = 1

		AND ODM_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_EQUIPO_SAP = #{codigoEquipoSap}


		AND CODIGO_PROVEEDOR_SAP = #{codigoProveedorSap}

mapper.insert:
INSERT INTO ODM( ODM_ID , CODIGO_EQUIPO_SAP , CODIGO_PROVEEDOR_SAP ) (
SELECT #{element.id} , #{element.codigoEquipoSap} , #{element.codigoProveedorSap} from dual
)

mapper.delete:
    DELETE FROM ODM
	WHERE 1 = 1

		AND ODM_ID = #{id, jdbcType=INTEGER}


		AND CODIGO_EQUIPO_SAP = #{codigoEquipoSap}


		AND CODIGO_PROVEEDOR_SAP = #{codigoProveedorSap}
